import { Pool, neonConfig } from '@neondatabase/serverless';
import { drizzle } from 'drizzle-orm/neon-serverless';
import ws from "ws";
import * as schema from "@shared/schema";

neonConfig.webSocketConstructor = ws;

if (!process.env.DATABASE_URL) {
  throw new Error(
    "DATABASE_URL must be set. Did you forget to provision a database?",
  );
}

export const pool = new Pool({ connectionString: process.env.DATABASE_URL });
export const db = drizzle({ client: pool, schema });

server/index.ts.content:
import express, { type Request, Response, NextFunction } from "express";
import { registerRoutes } from "./routes";
import { setupVite, serveStatic, log } from "./vite";

const app = express();
app.use(express.json());
app.use(express.urlencoded({ extended: false }));

app.use((req, res, next) => {
  const start = Date.now();
  const path = req.path;
  let capturedJsonResponse: Record<string, any> | undefined = undefined;

  const originalResJson = res.json;
  res.json = function (bodyJson, ...args) {
    capturedJsonResponse = bodyJson;
    return originalResJson.apply(res, [bodyJson, ...args]);
  };

  res.on("finish", () => {
    const duration = Date.now() - start;
    if (path.startsWith("/api")) {
      let logLine = `${req.method} ${path} ${res.statusCode} in ${duration}ms`;
      if (capturedJsonResponse) {
        logLine += ` :: ${JSON.stringify(capturedJsonResponse)}`;
      }

      if (logLine.length > 80) {
        logLine = logLine.slice(0, 79) + "â€¦";
      }

      log(logLine);
    }
  });

  next();
});

(async () => {
  const server = await registerRoutes(app);

  app.use((err: any, _req: Request, res: Response, _next: NextFunction) => {
    const status = err.status || err.statusCode || 500;
    const message = err.message || "Internal Server Error";

    res.status(status).json({ message });
    throw err;
  });

  // importantly only setup vite in development and after
  // setting up all the other routes so the catch-all route
  // doesn't interfere with the other routes
  if (app.get("env") === "development") {
    await setupVite(app, server);
  } else {
    serveStatic(app);
  }

  // ALWAYS serve the app on the port specified in the environment variable PORT
  // Other ports are firewalled. Default to 5000 if not specified.
  // this serves both the API and the client.
  // It is the only port that is not firewalled.
  const port = parseInt(process.env.PORT || '5000', 10);
  server.listen({
    port,
    host: "0.0.0.0",
    reusePort: true,
  }, () => {
    log(`serving on port ${port}`);
  });
})();

server/routes.ts.content:
import type { Express, Request, Response, NextFunction } from "express";
import { createServer, type Server } from "http";
import { emailService } from "./services/emailService";
import { smsService } from "./services/smsService";
import { prescriptionService } from "./services/prescriptionService";
import { voiceAgentService } from "./services/voiceAgentService";
import { storage } from "./storage";
import multer from 'multer';
import { requireAuth, validatePatientAccess, AuthenticatedRequest } from './middleware/auth';
import { healthAssessmentService } from './services/healthAssessmentService';
import { z } from 'zod';

export async function registerRoutes(app: Express): Promise<Server> {

  // Configure multer for file uploads
  const upload = multer({
    storage: multer.memoryStorage(),
    limits: {
      fileSize: 10 * 1024 * 1024, // 10MB limit
    },
    fileFilter: (req, file, cb) => {
      // Accept only audio files
      if (file.mimetype.startsWith('audio/')) {
        cb(null, true);
      } else {
        cb(new Error('Only audio files are allowed'));
      }
    }
  });

  // Multer error handling middleware
  const handleMulterError = (error: any, req: Request, res: Response, next: NextFunction) => {
    if (error instanceof multer.MulterError) {
      return res.status(400).json({
        error: 'File upload error',
        message: error.message
      });
    }
    if (error.message === 'Only audio files are allowed') {
      return res.status(400).json({
        error: 'Invalid file type',
        message: 'Only audio files are allowed'
      });
    }
    next(error);
  };

  // Voice Agent endpoints (secured)
  app.post("/api/voice/transcribe", requireAuth, validatePatientAccess, upload.single('audio'), async (req: AuthenticatedRequest, res) => {
    try {
      if (!req.file) {
        return res.status(400).json({ error: "No audio file provided" });
      }

      const { patientId, language, consultationType } = req.body;

      const transcript = await voiceAgentService.transcribeAudio(req.file.buffer, {
        patientId,
        language: language || 'en',
        consultationType: consultationType || 'telehealth'
      });

      res.json({ 
        success: true, 
        transcript,
        message: "Audio transcribed successfully"
      });
    } catch (error) {
      console.error("Error transcribing audio:", error);
      res.status(500).json({ error: "Failed to transcribe audio" });
    }
  });

  app.post("/api/voice/chat", requireAuth, validatePatientAccess, async (req: AuthenticatedRequest, res) => {
    try {
      const { 
        message, 
        conversationHistory = [], 
        patientId, 
        consultationType = 'telehealth',
        language = 'en'
      } = req.body;

      if (!message) {
        return res.status(400).json({ error: "Message is required" });
      }

      const response = await voiceAgentService.processVoiceInput(
        message,
        conversationHistory,
        { patientId, language, consultationType }
      );

      // Generate audio response if requested
      let audioBuffer = null;
      if (req.body.generateAudio) {
        audioBuffer = await voiceAgentService.generateTextToSpeech(response.textResponse, {
          language,
          consultationType
        });
      }

      res.json({
        success: true,
        response: response.textResponse,
        actions: response.actions || [],
        conversationId: response.conversationId,
        hasAudio: !!audioBuffer,
        audioData: audioBuffer ? audioBuffer.toString('base64') : null,
        message: "Voice agent response generated"
      });

      // Save conversation to database if patientId provided
      if (patientId && response.conversationId) {
        try {
          const messages = [
            ...conversationHistory,
            { role: 'user', content: message, timestamp: new Date() },
            { role: 'assistant', content: response.textResponse, timestamp: new Date() }
          ];

          await voiceAgentService.saveConversationToDatabase(
            patientId,
            messages,
            consultationType
          );
        } catch (dbError) {
          console.warn("Failed to save conversation to database:", dbError);
        }
      }
    } catch (error) {
      console.error("Error processing voice chat:", error);
      res.status(500).json({ error: "Failed to process voice chat" });
    }
  });

  app.post("/api/voice/tts", requireAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const { text, language = 'en', consultationType = 'telehealth' } = req.body;

      if (!text) {
        return res.status(400).json({ error: "Text is required" });
      }

      const audioBuffer = await voiceAgentService.generateTextToSpeech(text, {
        language,
        consultationType
      });

      if (!audioBuffer) {
        return res.json({ 
          success: true, 
          demoMode: true,
          message: "TTS demo mode - no audio generated" 
        });
      }

      res.setHeader('Content-Type', 'audio/mpeg');
      res.setHeader('Content-Length', audioBuffer.length);
      res.setHeader('Content-Disposition', 'attachment; filename="voice-response.mp3"');
      res.send(audioBuffer);
    } catch (error) {
      console.error("Error generating text-to-speech:", error);
      res.status(500).json({ error: "Failed to generate text-to-speech" });
    }
  });

  app.get("/api/voice/conversations/:patientId", requireAuth, validatePatientAccess, async (req: AuthenticatedRequest, res) => {
    try {
      const { patientId } = req.params;
      const { consultationType } = req.query;

      // Get consultations for the patient
      const consultations = await storage.getConsultationsByPatient(patientId);

      // Filter by consultation type if specified
      const filteredConsultations = consultationType 
        ? consultations.filter(c => c.consultationType === consultationType)
        : consultations;

      // Extract voice agent conversations (those with transcripts)
      const voiceConversations = filteredConsultations
        .filter(c => c.transcript)
        .map(c => ({
          id: c.id,
          consultationType: c.consultationType,
          reason: c.reason,
          status: c.status,
          createdAt: c.createdAt,
          transcript: c.transcript ? JSON.parse(c.transcript) : [],
          duration: c.duration
        }));

      res.json({
        success: true,
        conversations: voiceConversations,
        total: voiceConversations.length
      });
    } catch (error) {
      console.error("Error fetching voice conversations:", error);
      res.status(500).json({ error: "Failed to fetch voice conversations" });
    }
  });

  // Telehealth endpoints
  app.post("/api/telehealth/summary", async (req, res) => {
    try {
      const { contactData, channel, summaryType } = req.body;

      if (!contactData.consent) {
        return res.status(400).json({ error: "User consent required" });
      }

      const summary = {
        context: "telehealth",
        type: summaryType || "consultation",
        patient: contactData.name || "Demo User",
        timestamp: new Date().toISOString(),
        content: {
          reason: "Follow-up for lab results (demo)",
          preferredSlot: "Tomorrow 10:00",
          nextSteps: "Confirmation & reminders via SMS/WhatsApp"
        }
      };

      let sent = false;

      switch (channel) {
        case "email":
          sent = await emailService.sendSummary(contactData.email, summary);
          break;
        case "sms":
          sent = await smsService.sendSMS(contactData.phone, `Telehealth Summary: ${summary.content.reason}. Next steps: ${summary.content.nextSteps}`);
          break;
        case "whatsapp":
          sent = await smsService.sendWhatsApp(contactData.phone, `ðŸ“‹ *Telehealth Summary*\n\nReason: ${summary.content.reason}\nNext: ${summary.content.nextSteps}`);
          break;
        default:
          return res.status(400).json({ error: "Invalid channel" });
      }

      if (sent) {
        res.json({ success: true, message: "Summary sent successfully" });
      } else {
        res.status(500).json({ error: "Failed to send summary" });
      }
    } catch (error) {
      console.error("Error sending telehealth summary:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  app.post("/api/telehealth/prescription", async (req, res) => {
    try {
      const { contactData, items = [] } = req.body;

      const prescription = await prescriptionService.generatePrescription({
        patient: {
          name: contactData.name || "Demo User",
          email: contactData.email,
          phone: contactData.phone
        },
        items,
        context: "telehealth"
      });

      res.setHeader('Content-Type', 'text/html');
      res.send(prescription);
    } catch (error) {
      console.error("Error generating prescription:", error);
      res.status(500).json({ error: "Failed to generate prescription" });
    }
  });

  app.post("/api/telehealth/sick-note", async (req, res) => {
    try {
      const { contactData, reason, startDate, duration, country, employerEmail } = req.body;

      const sickNote = await prescriptionService.generateSickNote({
        patient: {
          name: contactData.name || "Demo User",
          email: contactData.email,
          phone: contactData.phone
        },
        reason: reason || "(not specified)",
        startDate: startDate || new Date().toISOString().split('T')[0],
        duration: duration || 3,
        country: country || "DE",
        employerEmail
      });

      res.setHeader('Content-Type', 'text/html');
      res.send(sickNote);
    } catch (error) {
      console.error("Error generating sick note:", error);
      res.status(500).json({ error: "Failed to generate sick note" });
    }
  });

  // Tele-pharmacy endpoints
  app.post("/api/telepharmacy/summary", async (req, res) => {
    try {
      const { contactData, channel, summaryType, cart = [] } = req.body;

      if (!contactData.consent) {
        return res.status(400).json({ error: "User consent required" });
      }

      const summary = {
        context: "telepharmacy",
        type: summaryType || "product",
        patient: contactData.name || "Demo User",
        timestamp: new Date().toISOString(),
        content: {
          products: cart.length ? cart.map((c: any) => `${c.id}x${c.qty}`).join(", ") : "(none)",
          advice: "Demo label guidance; check contraindications and interactions.",
          nextSteps: "Follow instructions; seek help if symptoms persist."
        }
      };

      let sent = false;

      switch (channel) {
        case "email":
          sent = await emailService.sendSummary(contactData.email, summary);
          break;
        case "sms":
          sent = await smsService.sendSMS(contactData.phone, `Pharmacy Summary: Products: ${summary.content.products}. Advice: ${summary.content.advice}`);
          break;
        case "whatsapp":
          sent = await smsService.sendWhatsApp(contactData.phone, `ðŸ¥ *Pharmacy Summary*\n\nProducts: ${summary.content.products}\nAdvice: ${summary.content.advice}`);
          break;
        default:
          return res.status(400).json({ error: "Invalid channel" });
      }

      if (sent) {
        res.json({ success: true, message: "Summary sent successfully" });
      } else {
        res.status(500).json({ error: "Failed to send summary" });
      }
    } catch (error) {
      console.error("Error sending telepharmacy summary:", error);
      res.status(500).json({ error: "Internal server error" });
    }
  });

  // Health Assessment endpoints (secured)
  app.get("/api/health/questionnaires", requireAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const questionnaires = healthAssessmentService.getHealthQuestionnaires();
      res.json({
        success: true,
        questionnaires,
        message: "Health questionnaires retrieved successfully"
      });
    } catch (error) {
      console.error("Error fetching health questionnaires:", error);
      res.status(500).json({ error: "Failed to fetch questionnaires" });
    }
  });

  // Health assessment request validation schema
  const healthAssessmentSchema = z.object({
    patientId: z.string().min(1),
    assessmentType: z.string().optional().default('symptom_check'),
    symptoms: z.array(z.string()).min(1, "At least one symptom is required"),
    responses: z.record(z.any()).optional().default({}),
    severity: z.record(z.number().min(1).max(10)).optional().default({}),
    duration: z.record(z.string()).optional().default({}),
    additionalInfo: z.string().optional().default('')
  });

  app.post("/api/health/assessment", requireAuth, validatePatientAccess, async (req: AuthenticatedRequest, res) => {
    try {
      const validatedData = healthAssessmentSchema.parse(req.body);
      const {
        patientId,
        assessmentType,
        symptoms,
        responses,
        severity,
        duration,
        additionalInfo
      } = validatedData;

      // Get patient data for context
      const patient = await storage.getPatient(patientId);
      const patientConfig = patient ? {
        patientAge: patient.dateOfBirth ? new Date().getFullYear() - new Date(patient.dateOfBirth).getFullYear() : undefined,
        patientGender: patient.gender || undefined,
        medicalHistory: [], // Would extract from medical records
        currentMedications: [], // Would extract from prescriptions
        allergies: [] // Would extract from patient data
      } : undefined;

      // Analyze symptoms with AI
      const aiAnalysis = await healthAssessmentService.analyzeSymptoms({
        symptoms,
        severity,
        duration,
        additionalInfo,
        patientConfig
      });

      // Create health assessment record
      const assessment = await storage.createHealthAssessment({
        patientId,
        assessmentType,
        symptoms,
        responses,
        aiAnalysis,
        followUpRequired: aiAnalysis.followUpDays ? aiAnalysis.followUpDays <= 3 : false,
        consultationRecommended: aiAnalysis.referralNeeded,
        status: 'completed'
      });

      res.json({
        success: true,
        assessment: {
          id: assessment.id,
          assessmentType: assessment.assessmentType,
          symptoms: assessment.symptoms,
          aiAnalysis: assessment.aiAnalysis,
          followUpRequired: assessment.followUpRequired,
          consultationRecommended: assessment.consultationRecommended,
          createdAt: assessment.createdAt
        },
        message: "Health assessment completed successfully"
      });

    } catch (error) {
      if (error instanceof z.ZodError) {
        return res.status(400).json({
          error: "Validation error",
          details: error.errors,
          message: "Please check your input and try again"
        });
      }
      console.error("Error creating health assessment:", error);
      res.status(500).json({ error: "Failed to complete health assessment" });
    }
  });

  app.get("/api/health/assessments/:patientId", requireAuth, validatePatientAccess, async (req: AuthenticatedRequest, res) => {
    try {
      const { patientId } = req.params;
      const { limit = 10, offset = 0 } = req.query;

      const assessments = await storage.getHealthAssessmentsByPatient(
        patientId,
        Number(limit),
        Number(offset)
      );

      res.json({
        success: true,
        assessments,
        count: assessments.length,
        message: "Health assessments retrieved successfully"
      });
    } catch (error) {
      console.error("Error fetching health assessments:", error);
      res.status(500).json({ error: "Failed to fetch health assessments" });
    }
  });

  app.get("/api/health/assessment/:id", requireAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const { id } = req.params;
      const assessment = await storage.getHealthAssessment(id);

      if (!assessment) {
        return res.status(404).json({ 
          error: "Assessment not found",
          message: "The requested health assessment does not exist"
        });
      }

      // Verify the assessment belongs to a patient accessible by this user
      const patient = await storage.getPatient(assessment.patientId);
      if (!patient || (req.user?.id !== 'demo-user-id' && patient.userId !== req.user?.id)) {
        return res.status(403).json({ 
          error: "Access denied",
          message: "You do not have permission to access this assessment"
        });
      }

      res.json({
        success: true,
        assessment,
        message: "Health assessment retrieved successfully"
      });
    } catch (error) {
      console.error("Error fetching health assessment:", error);
      res.status(500).json({ error: "Failed to fetch health assessment" });
    }
  });

  app.get("/api/health/emergency-symptoms", requireAuth, async (req: AuthenticatedRequest, res) => {
    try {
      const emergencySymptoms = healthAssessmentService.getEmergencySymptoms();
      res.json({
        success: true,
        emergencySymptoms,
        warning: "If you experience any of these symptoms, seek immediate medical attention",
        message: "Emergency symptoms list retrieved successfully"
      });
    } catch (error) {
      console.error("Error fetching emergency symptoms:", error);
      res.status(500).json({ error: "Failed to fetch emergency symptoms" });
    }
  });

  // Pharmacy endpoints
  app.get("/api/pharmacy/categories", async (req, res) => {
    try {
      const categories = await storage.getPharmacyCategories();
      res.json(categories);
    } catch (error) {
      console.error("Error fetching pharmacy categories:", error);
      res.status(500).json({ error: "Failed to fetch categories" });
    }
  });

  app.get("/api/pharmacy/products", async (req, res) => {
    try {
      const { categoryId } = req.query;

      let products;
      if (categoryId && typeof categoryId === 'string') {
        products = await storage.getPharmacyProductsByCategory(categoryId);
      } else {
        products = await storage.getPharmacyProducts();
      }

      res.json(products);
    } catch (error) {
      console.error("Error fetching pharmacy products:", error);
      res.status(500).json({ error: "Failed to fetch products" });
    }
  });

  app.get("/api/pharmacy/products/:id", async (req, res) => {
    try {
      const { id } = req.params;
      const product = await storage.getPharmacyProduct(id);

      if (!product) {
        return res.status(404).json({ error: "Product not found" });
      }

      res.json(product);
    } catch (error) {
      console.error("Error fetching pharmacy product:", error);
      res.status(500).json({ error: "Failed to fetch product" });
    }
  });

  // Add global error handling middleware
  app.use(handleMulterError);

  const httpServer = createServer(app);
  return httpServer;
}

server/storage.ts.content:
import { 
  type User, type InsertUser,
  type Patient, type InsertPatient,
  type MedicalRecord, type InsertMedicalRecord,
  type Consultation, type InsertConsultation,
  type Prescription, type InsertPrescription,
  type PharmacyCategory, type InsertPharmacyCategory,
  type PharmacyProduct, type InsertPharmacyProduct,
  type Appointment, type InsertAppointment,
  type Payment, type InsertPayment,
  type HealthAssessment, type InsertHealthAssessment,
  users, patients, medicalRecords, consultations, prescriptions,
  pharmacyCategories, pharmacyProducts, appointments, payments, healthAssessments
} from "@shared/schema";
import { db } from "./db";
import { eq, and, desc, asc } from "drizzle-orm";
import { securityService } from "./services/securityService";

export interface IStorage {
  // User operations
  getUser(id: string): Promise<User | undefined>;
  getUserByUsername(username: string): Promise<User | undefined>;
  createUser(user: InsertUser): Promise<User>;
  verifyUserPassword(username: string, password: string): Promise<User | null>;

  // Patient operations
  getPatient(id: string): Promise<Patient | undefined>;
  getPatientByEmail(email: string): Promise<Patient | undefined>;
  createPatient(patient: InsertPatient): Promise<Patient>;
  updatePatient(id: string, patient: Partial<InsertPatient>): Promise<Patient | undefined>;
  getPatientsByUserId(userId: string): Promise<Patient[]>;

  // Medical records operations
  getMedicalRecord(id: string): Promise<MedicalRecord | undefined>;
  getMedicalRecordsByPatient(patientId: string): Promise<MedicalRecord[]>;
  createMedicalRecord(record: InsertMedicalRecord): Promise<MedicalRecord>;
  updateMedicalRecord(id: string, record: Partial<InsertMedicalRecord>): Promise<MedicalRecord | undefined>;

  // Consultation operations
  getConsultation(id: string): Promise<Consultation | undefined>;
  getConsultationsByPatient(patientId: string): Promise<Consultation[]>;
  createConsultation(consultation: InsertConsultation): Promise<Consultation>;
  updateConsultation(id: string, consultation: Partial<InsertConsultation>): Promise<Consultation | undefined>;

  // Prescription operations
  getPrescription(id: string): Promise<Prescription | undefined>;
  getPrescriptionsByPatient(patientId: string): Promise<Prescription[]>;
  createPrescription(prescription: InsertPrescription): Promise<Prescription>;
  updatePrescription(id: string, prescription: Partial<InsertPrescription>): Promise<Prescription | undefined>;

  // Pharmacy category operations
  getPharmacyCategories(): Promise<PharmacyCategory[]>;
  getPharmacyCategory(id: string): Promise<PharmacyCategory | undefined>;
  createPharmacyCategory(category: InsertPharmacyCategory): Promise<PharmacyCategory>;

  // Pharmacy product operations
  getPharmacyProducts(): Promise<PharmacyProduct[]>;
  getPharmacyProductsByCategory(categoryId: string): Promise<PharmacyProduct[]>;
  getPharmacyProduct(id: string): Promise<PharmacyProduct | undefined>;
  createPharmacyProduct(product: InsertPharmacyProduct): Promise<PharmacyProduct>;
  updatePharmacyProduct(id: string, product: Partial<InsertPharmacyProduct>): Promise<PharmacyProduct | undefined>;
  searchPharmacyProducts(query: string): Promise<PharmacyProduct[]>;

  // Appointment operations
  getAppointment(id: string): Promise<Appointment | undefined>;
  getAppointmentsByPatient(patientId: string): Promise<Appointment[]>;
  createAppointment(appointment: InsertAppointment): Promise<Appointment>;
  updateAppointment(id: string, appointment: Partial<InsertAppointment>): Promise<Appointment | undefined>;

  // Payment operations
  getPayment(id: string): Promise<Payment | undefined>;
  getPaymentsByPatient(patientId: string): Promise<Payment[]>;
  createPayment(payment: InsertPayment): Promise<Payment>;
  updatePayment(id: string, payment: Partial<InsertPayment>): Promise<Payment | undefined>;

  // Health assessment operations
  getHealthAssessment(id: string): Promise<HealthAssessment | undefined>;
  getHealthAssessmentsByPatient(patientId: string): Promise<HealthAssessment[]>;
  createHealthAssessment(assessment: InsertHealthAssessment): Promise<HealthAssessment>;
  updateHealthAssessment(id: string, assessment: Partial<InsertHealthAssessment>): Promise<HealthAssessment | undefined>;
}

export class DatabaseStorage implements IStorage {
  // User operations
  async getUser(id: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.id, id)).limit(1);
    return result[0];
  }

  async getUserByUsername(username: string): Promise<User | undefined> {
    const result = await db.select().from(users).where(eq(users.username, username)).limit(1);
    return result[0];
  }

  async createUser(user: InsertUser): Promise<User> {
    // Hash the password before storing
    const hashedPassword = await securityService.hashPassword(user.password);
    const userWithHashedPassword = { ...user, password: hashedPassword };

    const result = await db.insert(users).values(userWithHashedPassword).returning();
    return result[0];
  }

  async verifyUserPassword(username: string, password: string): Promise<User | null> {
    const user = await this.getUserByUsername(username);
    if (!user) return null;

    const isValidPassword = await securityService.verifyPassword(password, user.password);
    return isValidPassword ? user : null;
  }

  // Patient operations
  async getPatient(id: string): Promise<Patient | undefined> {
    const result = await db.select().from(patients).where(eq(patients.id, id)).limit(1);
    return result[0] ? this.decryptPatientData(result[0]) : undefined;
  }

  async getPatientByEmail(email: string): Promise<Patient | undefined> {
    const result = await db.select().from(patients).where(eq(patients.email, email)).limit(1);
    return result[0] ? this.decryptPatientData(result[0]) : undefined;
  }

  async createPatient(patient: InsertPatient): Promise<Patient> {
    // Encrypt sensitive patient data
    const encryptedPatient = {
      ...patient,
      medicalHistory: securityService.encryptSensitiveField(patient.medicalHistory),
      insuranceInfo: securityService.encryptSensitiveField(patient.insuranceInfo),
      emergencyContact: securityService.encryptSensitiveField(patient.emergencyContact),
    };

    const result = await db.insert(patients).values(encryptedPatient as any).returning();
    return this.decryptPatientData(result[0]);
  }

  async updatePatient(id: string, patient: Partial<InsertPatient>): Promise<Patient | undefined> {
    // Encrypt sensitive fields if they're being updated
    const encryptedUpdate = { ...patient };
    if (patient.medicalHistory !== undefined) {
      encryptedUpdate.medicalHistory = securityService.encryptSensitiveField(patient.medicalHistory);
    }
    if (patient.insuranceInfo !== undefined) {
      encryptedUpdate.insuranceInfo = securityService.encryptSensitiveField(patient.insuranceInfo);
    }
    if (patient.emergencyContact !== undefined) {
      encryptedUpdate.emergencyContact = securityService.encryptSensitiveField(patient.emergencyContact);
    }

    const result = await db.update(patients).set(encryptedUpdate as any).where(eq(patients.id, id)).returning();
    return result[0] ? this.decryptPatientData(result[0]) : undefined;
  }

  private decryptPatientData(patient: Patient): Patient {
    return {
      ...patient,
      medicalHistory: securityService.decryptSensitiveField(patient.medicalHistory),
      insuranceInfo: securityService.decryptSensitiveField(patient.insuranceInfo),
      emergencyContact: securityService.decryptSensitiveField(patient.emergencyContact),
    };
  }

  async getPatientsByUserId(userId: string): Promise<Patient[]> {
    const results = await db.select().from(patients).where(eq(patients.userId, userId));
    return results.map(patient => this.decryptPatientData(patient));
  }

  // Medical records operations
  async getMedicalRecord(id: string): Promise<MedicalRecord | undefined> {
    const result = await db.select().from(medicalRecords).where(eq(medicalRecords.id, id)).limit(1);
    return result[0] ? this.decryptMedicalRecord(result[0]) : undefined;
  }

  async getMedicalRecordsByPatient(patientId: string): Promise<MedicalRecord[]> {
    const results = await db.select().from(medicalRecords)
      .where(eq(medicalRecords.patientId, patientId))
      .orderBy(desc(medicalRecords.createdAt));
    return results.map(record => this.decryptMedicalRecord(record));
  }

  async createMedicalRecord(record: InsertMedicalRecord): Promise<MedicalRecord> {
    // Encrypt sensitive medical content
    const encryptedRecord = {
      ...record,
      content: securityService.encryptMedicalData(record.content),
    };

    const result = await db.insert(medicalRecords).values(encryptedRecord).returning();
    return this.decryptMedicalRecord(result[0]);
  }

  async updateMedicalRecord(id: string, record: Partial<InsertMedicalRecord>): Promise<MedicalRecord | undefined> {
    // Encrypt content if it's being updated
    const encryptedUpdate = { ...record };
    if (record.content !== undefined) {
      encryptedUpdate.content = securityService.encryptMedicalData(record.content);
    }

    const result = await db.update(medicalRecords).set(encryptedUpdate).where(eq(medicalRecords.id, id)).returning();
    return result[0] ? this.decryptMedicalRecord(result[0]) : undefined;
  }

  private decryptMedicalRecord(record: MedicalRecord): MedicalRecord {
    return {
      ...record,
      content: securityService.decryptMedicalData(record.content),
    };
  }

  // Consultation operations
  async getConsultation(id: string): Promise<Consultation | undefined> {
    const result = await db.select().from(consultations).where(eq(consultations.id, id)).limit(1);
    return result[0];
  }

  async getConsultationsByPatient(patientId: string): Promise<Consultation[]> {
    return await db.select().from(consultations)
      .where(eq(consultations.patientId, patientId))
      .orderBy(desc(consultations.createdAt));
  }

  async createConsultation(consultation: InsertConsultation): Promise<Consultation> {
    const result = await db.insert(consultations).values(consultation).returning();
    return result[0];
  }

  async updateConsultation(id: string, consultation: Partial<InsertConsultation>): Promise<Consultation | undefined> {
    const result = await db.update(consultations).set(consultation).where(eq(consultations.id, id)).returning();
    return result[0];
  }

  // Prescription operations
  async getPrescription(id: string): Promise<Prescription | undefined> {
    const result = await db.select().from(prescriptions).where(eq(prescriptions.id, id)).limit(1);
    return result[0];
  }

  async getPrescriptionsByPatient(patientId: string): Promise<Prescription[]> {
    return await db.select().from(prescriptions)
      .where(eq(prescriptions.patientId, patientId))
      .orderBy(desc(prescriptions.createdAt));
  }

  async createPrescription(prescription: InsertPrescription): Promise<Prescription> {
    const result = await db.insert(prescriptions).values(prescription as any).returning();
    return result[0];
  }

  async updatePrescription(id: string, prescription: Partial<InsertPrescription>): Promise<Prescription | undefined> {
    const result = await db.update(prescriptions).set(prescription as any).where(eq(prescriptions.id, id)).returning();
    return result[0];
  }

  // Pharmacy category operations
  async getPharmacyCategories(): Promise<PharmacyCategory[]> {
    return await db.select().from(pharmacyCategories)
      .where(eq(pharmacyCategories.isActive, true))
      .orderBy(asc(pharmacyCategories.sortOrder));
  }

  async getPharmacyCategory(id: string): Promise<PharmacyCategory | undefined> {
    const result = await db.select().from(pharmacyCategories).where(eq(pharmacyCategories.id, id)).limit(1);
    return result[0];
  }

  async createPharmacyCategory(category: InsertPharmacyCategory): Promise<PharmacyCategory> {
    const result = await db.insert(pharmacyCategories).values(category).returning();
    return result[0];
  }

  // Pharmacy product operations
  async getPharmacyProducts(): Promise<PharmacyProduct[]> {
    return await db.select().from(pharmacyProducts)
      .where(eq(pharmacyProducts.isActive, true))
      .orderBy(asc(pharmacyProducts.name));
  }

  async getPharmacyProductsByCategory(categoryId: string): Promise<PharmacyProduct[]> {
    return await db.select().from(pharmacyProducts)
      .where(and(eq(pharmacyProducts.categoryId, categoryId), eq(pharmacyProducts.isActive, true)))
      .orderBy(asc(pharmacyProducts.name));
  }

  async getPharmacyProduct(id: string): Promise<PharmacyProduct | undefined> {
    const result = await db.select().from(pharmacyProducts).where(eq(pharmacyProducts.id, id)).limit(1);
    return result[0];
  }

  async createPharmacyProduct(product: InsertPharmacyProduct): Promise<PharmacyProduct> {
    const result = await db.insert(pharmacyProducts).values(product).returning();
    return result[0];
  }

  async updatePharmacyProduct(id: string, product: Partial<InsertPharmacyProduct>): Promise<PharmacyProduct | undefined> {
    const result = await db.update(pharmacyProducts).set(product).where(eq(pharmacyProducts.id, id)).returning();
    return result[0];
  }

  async searchPharmacyProducts(query: string): Promise<PharmacyProduct[]> {
    const searchTerm = `%${query.toLowerCase()}%`;
    return await db.select().from(pharmacyProducts)
      .where(and(
        eq(pharmacyProducts.isActive, true),
        // Note: This is a simplified search - in production you'd use full-text search
      ));
  }

  // Appointment operations
  async getAppointment(id: string): Promise<Appointment | undefined> {
    const result = await db.select().from(appointments).where(eq(appointments.id, id)).limit(1);
    return result[0];
  }

  async getAppointmentsByPatient(patientId: string): Promise<Appointment[]> {
    return await db.select().from(appointments)
      .where(eq(appointments.patientId, patientId))
      .orderBy(desc(appointments.scheduledAt));
  }

  async createAppointment(appointment: InsertAppointment): Promise<Appointment> {
    const result = await db.insert(appointments).values(appointment).returning();
    return result[0];
  }

  async updateAppointment(id: string, appointment: Partial<InsertAppointment>): Promise<Appointment | undefined> {
    const result = await db.update(appointments).set(appointment).where(eq(appointments.id, id)).returning();
    return result[0];
  }

  // Payment operations
  async getPayment(id: string): Promise<Payment | undefined> {
    const result = await db.select().from(payments).where(eq(payments.id, id)).limit(1);
    return result[0];
  }

  async getPaymentsByPatient(patientId: string): Promise<Payment[]> {
    return await db.select().from(payments)
      .where(eq(payments.patientId, patientId))
      .orderBy(desc(payments.createdAt));
  }

  async createPayment(payment: InsertPayment): Promise<Payment> {
    const result = await db.insert(payments).values(payment as any).returning();
    return result[0];
  }

  async updatePayment(id: string, payment: Partial<InsertPayment>): Promise<Payment | undefined> {
    const result = await db.update(payments).set(payment as any).where(eq(payments.id, id)).returning();
    return result[0];
  }

  // Health assessment operations
  async getHealthAssessment(id: string): Promise<HealthAssessment | undefined> {
    const result = await db.select().from(healthAssessments).where(eq(healthAssessments.id, id)).limit(1);
    return result[0];
  }

  async getHealthAssessmentsByPatient(patientId: string): Promise<HealthAssessment[]> {
    return await db.select().from(healthAssessments)
      .where(eq(healthAssessments.patientId, patientId))
      .orderBy(desc(healthAssessments.createdAt));
  }

  async createHealthAssessment(assessment: InsertHealthAssessment): Promise<HealthAssessment> {
    const result = await db.insert(healthAssessments).values(assessment as any).returning();
    return result[0];
  }

  async updateHealthAssessment(id: string, assessment: Partial<InsertHealthAssessment>): Promise<HealthAssessment | undefined> {
    const result = await db.update(healthAssessments).set(assessment as any).where(eq(healthAssessments.id, id)).returning();
    return result[0];
  }
}

export const storage = new DatabaseStorage();

