              className="bg-primary hover:bg-primary/90"
              onClick={() => window.open(doctorHandoffUrl, "_blank")}
              data-testid="button-book-doctor"
            >
              <i className="fas fa-calendar-check mr-2"></i>
              {t.bookDoctor}
