        categoryId,
      });
      console.log(`âœ“ Created product: ${productData.name}`);
    } catch (error) {
      console.log(`Product ${productData.name} might already exist`);
    }
  }

  console.log("ðŸŽ‰ Pharmacy data seeding completed!");
}

