import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { 
  FileText, 
  Calendar, 
  AlertTriangle, 
  CheckCircle, 
  Clock,
  TrendingUp,
  Heart
} from 'lucide-react';

interface AIHealthAnalysis {
  diagnosis: string[];
  recommendations: string[];
  urgencyLevel: 'low' | 'medium' | 'high' | 'emergency';
  referralNeeded: boolean;
  riskScore: number;
  followUpDays?: number;
  emergencyWarning?: string;
}

interface HealthAssessment {
  id: string;
  assessmentType: string;
  symptoms: string[];
  aiAnalysis: AIHealthAnalysis;
  followUpRequired: boolean;
  consultationRecommended: boolean;
  createdAt: string;
}

interface AssessmentHistoryProps {
  assessments: HealthAssessment[];
  onViewAssessment: (assessment: HealthAssessment) => void;
}

export function AssessmentHistory({ assessments, onViewAssessment }: AssessmentHistoryProps) {
  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
        return 'destructive';
      case 'high':
        return 'destructive';
      case 'medium':
        return 'secondary';
      case 'low':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getUrgencyIcon = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
      case 'high':
        return <AlertTriangle className="h-4 w-4" />;
      case 'medium':
        return <Clock className="h-4 w-4" />;
      case 'low':
        return <CheckCircle className="h-4 w-4" />;
      default:
        return <Heart className="h-4 w-4" />;
    }
  };

  const getAssessmentTypeDisplay = (type: string) => {
    switch (type) {
      case 'symptom_check':
        return 'Symptom Check';
      case 'wellness_check':
        return 'Wellness Check';
      case 'risk_assessment':
        return 'Risk Assessment';
      default:
        return type.charAt(0).toUpperCase() + type.slice(1);
    }
  };

  if (assessments.length === 0) {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Assessment History
          </CardTitle>
          <CardDescription>
            Your previous health assessments will appear here
          </CardDescription>
        </CardHeader>
        <CardContent className="text-center py-8">
          <FileText className="h-12 w-12 mx-auto text-muted-foreground mb-4" />
          <h3 className="text-lg font-medium mb-2">No assessments yet</h3>
          <p className="text-muted-foreground mb-4">
            Start your first health assessment to track your health over time
          </p>
          <Button 
            onClick={() => window.location.reload()} 
            data-testid="button-start-first-assessment"
          >
            Start Assessment
          </Button>
        </CardContent>
      </Card>
    );
  }

  return (
    <div className="space-y-6">
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <FileText className="h-5 w-5" />
            Assessment History
          </CardTitle>
          <CardDescription>
            View and track your previous health assessments
          </CardDescription>
        </CardHeader>
      </Card>

      <div className="space-y-4">
        {assessments.map((assessment) => (
          <Card key={assessment.id} className="hover:shadow-md transition-shadow">
            <CardHeader>
              <div className="flex items-start justify-between">
                <div className="space-y-1">
                  <CardTitle className="text-lg flex items-center gap-2">
                    <Calendar className="h-4 w-4" />
                    {getAssessmentTypeDisplay(assessment.assessmentType)}
                  </CardTitle>
                  <CardDescription>
                    {new Date(assessment.createdAt).toLocaleDateString('en-US', {
                      year: 'numeric',
                      month: 'long',
                      day: 'numeric',
                      hour: '2-digit',
                      minute: '2-digit'
                    })}
                  </CardDescription>
                </div>
                <Badge 
                  variant={getUrgencyColor(assessment.aiAnalysis.urgencyLevel)} 
                  className="flex items-center gap-1"
                >
                  {getUrgencyIcon(assessment.aiAnalysis.urgencyLevel)}
                  {assessment.aiAnalysis.urgencyLevel.charAt(0).toUpperCase() + 
                   assessment.aiAnalysis.urgencyLevel.slice(1)}
                </Badge>
              </div>
            </CardHeader>
            <CardContent className="space-y-4">
              {/* Symptoms Summary */}
              <div>
                <h4 className="font-medium mb-2">Symptoms ({assessment.symptoms.length})</h4>
                <div className="flex flex-wrap gap-1">
                  {assessment.symptoms.slice(0, 4).map((symptom) => (
                    <Badge key={symptom} variant="outline" className="text-xs">
                      {symptom}
                    </Badge>
                  ))}
                  {assessment.symptoms.length > 4 && (
                    <Badge variant="outline" className="text-xs">
                      +{assessment.symptoms.length - 4} more
                    </Badge>
                  )}
                </div>
              </div>

              {/* Key Metrics */}
              <div className="grid grid-cols-2 md:grid-cols-3 gap-4 text-sm">
                <div className="flex items-center gap-2">
                  <TrendingUp className="h-4 w-4 text-muted-foreground" />
                  <span>Risk Score: {assessment.aiAnalysis.riskScore}/100</span>
                </div>

                {assessment.aiAnalysis.followUpDays && (
                  <div className="flex items-center gap-2">
                    <Clock className="h-4 w-4 text-muted-foreground" />
                    <span>Follow-up: {assessment.aiAnalysis.followUpDays} days</span>
                  </div>
                )}

                {assessment.consultationRecommended && (
                  <div className="flex items-center gap-2">
                    <Heart className="h-4 w-4 text-red-500" />
                    <span className="text-red-600">Consultation Recommended</span>
                  </div>
                )}
              </div>

              {/* Primary Diagnosis */}
              {assessment.aiAnalysis.diagnosis.length > 0 && (
                <div>
                  <h4 className="font-medium mb-1">Primary Assessment</h4>
                  <p className="text-sm text-muted-foreground">
                    {assessment.aiAnalysis.diagnosis[0]}
                    {assessment.aiAnalysis.diagnosis.length > 1 && 
                      ` (and ${assessment.aiAnalysis.diagnosis.length - 1} other${
                        assessment.aiAnalysis.diagnosis.length > 2 ? 's' : ''
                      })`
                    }
                  </p>
                </div>
              )}

              {/* Emergency Warning */}
              {assessment.aiAnalysis.emergencyWarning && (
                <div className="bg-red-50 border border-red-200 rounded-md p-3">
                  <div className="flex items-start gap-2">
                    <AlertTriangle className="h-4 w-4 text-red-600 mt-0.5 flex-shrink-0" />
                    <p className="text-sm text-red-800">
                      {assessment.aiAnalysis.emergencyWarning}
                    </p>
                  </div>
                </div>
              )}

              {/* Actions */}
              <div className="flex gap-2 pt-2">
                <Button 
                  variant="outline" 
                  size="sm"
                  onClick={() => onViewAssessment(assessment)}
                  data-testid={`button-view-assessment-${assessment.id}`}
                >
                  View Details
                </Button>

                {assessment.consultationRecommended && (
                  <Button 
                    variant="outline" 
                    size="sm"
                    data-testid={`button-book-consultation-${assessment.id}`}
                  >
                    Book Consultation
                  </Button>
                )}
              </div>
            </CardContent>
          </Card>
        ))}
      </div>

      {/* Summary Stats */}
      {assessments.length > 1 && (
        <Card>
          <CardHeader>
            <CardTitle className="text-lg">Health Summary</CardTitle>
          </CardHeader>
          <CardContent>
            <div className="grid grid-cols-2 md:grid-cols-4 gap-4 text-center">
              <div>
                <p className="text-2xl font-bold">{assessments.length}</p>
                <p className="text-sm text-muted-foreground">Total Assessments</p>
              </div>

              <div>
                <p className="text-2xl font-bold">
                  {Math.round(
                    assessments.reduce((sum, a) => sum + a.aiAnalysis.riskScore, 0) / 
                    assessments.length
                  )}
                </p>
                <p className="text-sm text-muted-foreground">Avg Risk Score</p>
              </div>

              <div>
                <p className="text-2xl font-bold">
                  {assessments.filter(a => a.consultationRecommended).length}
                </p>
                <p className="text-sm text-muted-foreground">Consultations Recommended</p>
              </div>

              <div>
                <p className="text-2xl font-bold">
                  {assessments.filter(a => a.followUpRequired).length}
                </p>
                <p className="text-sm text-muted-foreground">Follow-ups Required</p>
              </div>
            </div>
          </CardContent>
        </Card>
      )}
    </div>
  );
}

client/src/components/SymptomChecker.tsx.content:
import { useState } from 'react';
import { useForm } from 'react-hook-form';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Checkbox } from '@/components/ui/checkbox';
import { Textarea } from '@/components/ui/textarea';
import { Label } from '@/components/ui/label';
import { Separator } from '@/components/ui/separator';
import { Badge } from '@/components/ui/badge';
import { Slider } from '@/components/ui/slider';
import { RadioGroup, RadioGroupItem } from '@/components/ui/radio-group';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { 
  Thermometer, 
  Clock, 
  AlertTriangle, 
  CheckCircle,
  Activity,
  Brain
} from 'lucide-react';

interface Questionnaire {
  id: string;
  title: string;
  description: string;
  questions: Array<{
    id: string;
    type: string;
    question: string;
    options?: string[];
    required: boolean;
  }>;
}

interface SymptomCheckerProps {
  questionnaires: Questionnaire[];
  onSubmit: (data: any) => Promise<void>;
  isLoading: boolean;
}

export function SymptomChecker({ questionnaires, onSubmit, isLoading }: SymptomCheckerProps) {
  const [selectedSymptoms, setSelectedSymptoms] = useState<string[]>([]);
  const [symptomSeverity, setSymptomSeverity] = useState<Record<string, number>>({});
  const [symptomDuration, setSymptomDuration] = useState<Record<string, string>>({});
  const [currentStep, setCurrentStep] = useState<'symptoms' | 'details' | 'review'>('symptoms');
  const [additionalInfo, setAdditionalInfo] = useState('');
  const [temperature, setTemperature] = useState('');

  const { register, handleSubmit, setValue, watch } = useForm();

  // Get the symptom checker questionnaire
  const symptomQuestionnaire = questionnaires.find(q => q.id === 'symptom_checker');
  const symptomsQuestion = symptomQuestionnaire?.questions.find(q => q.id === 'primary_symptoms');

  const commonSymptoms = symptomsQuestion?.options || [
    'Headache', 'Fever', 'Cough', 'Sore throat', 'Fatigue',
    'Nausea', 'Vomiting', 'Diarrhea', 'Abdominal pain',
    'Chest pain', 'Difficulty breathing', 'Dizziness',
    'Muscle aches', 'Joint pain', 'Skin rash'
  ];

  const emergencySymptoms = [
    'Chest pain', 'Difficulty breathing', 'Severe bleeding', 
    'Loss of consciousness', 'Severe allergic reaction'
  ];

  const durationOptions = [
    'Less than 24 hours',
    '1-3 days',
    '4-7 days', 
    '1-2 weeks',
    'More than 2 weeks'
  ];

  const temperatureOptions = [
    'No fever (under 100Â°F/37.8Â°C)',
    'Low fever (100-101Â°F/37.8-38.3Â°C)',
    'Moderate fever (101-103Â°F/38.3-39.4Â°C)',
    'High fever (over 103Â°F/39.4Â°C)',
    'Haven\'t measured'
  ];

  const handleSymptomToggle = (symptom: string) => {
    setSelectedSymptoms(prev => {
      const isSelected = prev.includes(symptom);
      if (isSelected) {
        // Remove symptom and its associated data
        const newSymptoms = prev.filter(s => s !== symptom);
        setSymptomSeverity(prevSev => {
          const newSev = { ...prevSev };
          delete newSev[symptom];
          return newSev;
        });
        setSymptomDuration(prevDur => {
          const newDur = { ...prevDur };
          delete newDur[symptom];
          return newDur;
        });
        return newSymptoms;
      } else {
        return [...prev, symptom];
      }
    });
  };

  const handleSeverityChange = (symptom: string, severity: number[]) => {
    setSymptomSeverity(prev => ({
      ...prev,
      [symptom]: severity[0]
    }));
  };

  const handleDurationChange = (symptom: string, duration: string) => {
    setSymptomDuration(prev => ({
      ...prev,
      [symptom]: duration
    }));
  };

  const canProceedToDetails = selectedSymptoms.length > 0;
  const canProceedToReview = selectedSymptoms.length > 0 && selectedSymptoms.every(symptom => 
    symptomSeverity[symptom] !== undefined && symptomDuration[symptom] !== undefined && symptomDuration[symptom] !== ''
  );

  const hasEmergencySymptoms = selectedSymptoms.some(symptom =>
    emergencySymptoms.some(emergency => 
      symptom.toLowerCase().includes(emergency.toLowerCase())
    )
  );

  const handleFinalSubmit = async () => {
    try {
      await onSubmit({
        assessmentType: 'symptom_check',
        symptoms: selectedSymptoms,
        severity: symptomSeverity,
        duration: symptomDuration,
        additionalInfo,
        responses: {
          temperature,
          additional_info: additionalInfo
        }
      });
    } catch (error) {
      console.error('Error submitting assessment:', error);
    }
  };

  const renderStepIndicator = () => (
    <div className="flex justify-center space-x-2 mb-6">
      {['symptoms', 'details', 'review'].map((step, index) => (
        <div key={step} className="flex items-center">
          <div className={`
            w-8 h-8 rounded-full flex items-center justify-center text-sm font-medium
            ${currentStep === step ? 'bg-primary text-primary-foreground' : 
              ['symptoms', 'details'].includes(step) && currentStep === 'review' ? 'bg-green-500 text-white' :
              'bg-muted text-muted-foreground'}
          `}>
            {currentStep === step ? (index + 1) : 
             ['symptoms', 'details'].includes(step) && currentStep === 'review' ? 'âœ“' : (index + 1)}
          </div>
          {index < 2 && (
            <div className={`w-12 h-0.5 mx-2 ${
              currentStep === 'review' || (currentStep === 'details' && step === 'symptoms') 
                ? 'bg-green-500' : 'bg-muted'
            }`} />
          )}
        </div>
      ))}
    </div>
  );

  if (currentStep === 'symptoms') {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Activity className="h-5 w-5" />
            Select Your Symptoms
          </CardTitle>
          <CardDescription>
            Choose all symptoms you are currently experiencing
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {renderStepIndicator()}

          {hasEmergencySymptoms && (
            <Alert variant="destructive">
              <AlertTriangle className="h-4 w-4" />
              <AlertDescription>
                You have selected symptoms that may require immediate medical attention. 
                Please consider seeking emergency care if symptoms are severe.
              </AlertDescription>
            </Alert>
          )}

          <div className="grid grid-cols-2 md:grid-cols-3 gap-3">
            {commonSymptoms.map((symptom) => (
              <div key={symptom} className="flex items-center space-x-2">
                <Checkbox
                  id={symptom}
                  checked={selectedSymptoms.includes(symptom)}
                  onCheckedChange={() => handleSymptomToggle(symptom)}
                  data-testid={`symptom-${symptom.toLowerCase().replace(/\s+/g, '-')}`}
                />
                <Label 
                  htmlFor={symptom} 
                  className={`text-sm cursor-pointer ${
                    emergencySymptoms.some(emergency => 
                      symptom.toLowerCase().includes(emergency.toLowerCase())
                    ) ? 'text-red-600 font-medium' : ''
                  }`}
                >
                  {symptom}
                </Label>
              </div>
            ))}
          </div>

          {selectedSymptoms.length > 0 && (
            <div className="space-y-2">
              <p className="font-medium">Selected symptoms:</p>
              <div className="flex flex-wrap gap-2">
                {selectedSymptoms.map((symptom) => (
                  <Badge key={symptom} variant="secondary">
                    {symptom}
                    <button
                      onClick={() => handleSymptomToggle(symptom)}
                      className="ml-2 hover:text-destructive"
                      data-testid={`remove-symptom-${symptom.toLowerCase().replace(/\s+/g, '-')}`}
                    >
                      Ã—
                    </button>
                  </Badge>
                ))}
              </div>
            </div>
          )}

          <div className="flex justify-end">
            <Button 
              onClick={() => setCurrentStep('details')}
              disabled={!canProceedToDetails}
              data-testid="button-proceed-to-details"
            >
              Continue to Details
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (currentStep === 'details') {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Clock className="h-5 w-5" />
            Symptom Details
          </CardTitle>
          <CardDescription>
            Provide details about each symptom's severity and duration
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {renderStepIndicator()}

          <div className="space-y-6">
            {selectedSymptoms.map((symptom) => (
              <Card key={symptom} className="p-4">
                <h4 className="font-medium mb-4">{symptom}</h4>

                <div className="space-y-4">
                  <div>
                    <Label className="text-sm font-medium mb-2 block">
                      Severity (1 = mild, 10 = severe): {symptomSeverity[symptom] || 5}
                    </Label>
                    <Slider
                      value={[symptomSeverity[symptom] || 5]}
                      onValueChange={(value) => handleSeverityChange(symptom, value)}
                      max={10}
                      min={1}
                      step={1}
                      className="w-full"
                      data-testid={`severity-slider-${symptom.toLowerCase().replace(/\s+/g, '-')}`}
                    />
                  </div>

                  <div>
                    <Label className="text-sm font-medium mb-2 block">Duration</Label>
                    <RadioGroup
                      value={symptomDuration[symptom] || ''}
                      onValueChange={(value) => handleDurationChange(symptom, value)}
                    >
                      {durationOptions.map((duration) => (
                        <div key={duration} className="flex items-center space-x-2">
                          <RadioGroupItem 
                            value={duration} 
                            id={`${symptom}-${duration}`}
                            data-testid={`duration-${symptom.toLowerCase().replace(/\s+/g, '-')}-${duration.toLowerCase().replace(/\s+/g, '-')}`}
                          />
                          <Label htmlFor={`${symptom}-${duration}`} className="text-sm">
                            {duration}
                          </Label>
                        </div>
                      ))}
                    </RadioGroup>
                  </div>
                </div>
              </Card>
            ))}
          </div>

          <Separator />

          <div className="space-y-4">
            <div>
              <Label className="text-sm font-medium mb-2 block flex items-center gap-2">
                <Thermometer className="h-4 w-4" />
                Temperature Status
              </Label>
              <RadioGroup value={temperature} onValueChange={setTemperature}>
                {temperatureOptions.map((temp) => (
                  <div key={temp} className="flex items-center space-x-2">
                    <RadioGroupItem value={temp} id={temp} data-testid={`temperature-${temp.toLowerCase().replace(/\s+/g, '-')}`} />
                    <Label htmlFor={temp} className="text-sm">{temp}</Label>
                  </div>
                ))}
              </RadioGroup>
            </div>

            <div>
              <Label htmlFor="additional-info" className="text-sm font-medium mb-2 block">
                Additional Information (Optional)
              </Label>
              <Textarea
                id="additional-info"
                placeholder="Describe any other symptoms, relevant medical history, or concerns..."
                value={additionalInfo}
                onChange={(e) => setAdditionalInfo(e.target.value)}
                rows={3}
                data-testid="input-additional-info"
              />
            </div>
          </div>

          <div className="flex justify-between">
            <Button 
              variant="outline" 
              onClick={() => setCurrentStep('symptoms')}
              data-testid="button-back-to-symptoms"
            >
              Back
            </Button>
            <Button 
              onClick={() => setCurrentStep('review')}
              disabled={!canProceedToReview}
              data-testid="button-proceed-to-review"
            >
              Review & Submit
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  if (currentStep === 'review') {
    return (
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <Brain className="h-5 w-5" />
            Review Your Assessment
          </CardTitle>
          <CardDescription>
            Please review your information before submitting for AI analysis
          </CardDescription>
        </CardHeader>
        <CardContent className="space-y-6">
          {renderStepIndicator()}

          <div className="space-y-4">
            <div>
              <h4 className="font-medium mb-2">Symptoms ({selectedSymptoms.length})</h4>
              <div className="space-y-2">
                {selectedSymptoms.map((symptom) => (
                  <div key={symptom} className="flex justify-between items-center p-2 bg-muted rounded">
                    <span className="font-medium">{symptom}</span>
                    <div className="text-sm text-muted-foreground">
                      Severity: {symptomSeverity[symptom]}/10 â€¢ Duration: {symptomDuration[symptom]}
                    </div>
                  </div>
                ))}
              </div>
            </div>

            {temperature && (
              <div>
                <h4 className="font-medium mb-2">Temperature</h4>
                <p className="text-sm text-muted-foreground">{temperature}</p>
              </div>
            )}

            {additionalInfo && (
              <div>
                <h4 className="font-medium mb-2">Additional Information</h4>
                <p className="text-sm text-muted-foreground">{additionalInfo}</p>
              </div>
            )}
          </div>

          <Alert>
            <AlertTriangle className="h-4 w-4" />
            <AlertDescription>
              This assessment will be analyzed by AI to provide health insights. 
              Results are for informational purposes only and do not replace professional medical advice.
            </AlertDescription>
          </Alert>

          <div className="flex justify-between">
            <Button 
              variant="outline" 
              onClick={() => setCurrentStep('details')}
              data-testid="button-back-to-details"
            >
              Back to Edit
            </Button>
            <Button 
              onClick={handleFinalSubmit}
              disabled={isLoading}
              data-testid="button-submit-assessment"
            >
              {isLoading ? 'Analyzing...' : 'Submit for AI Analysis'}
            </Button>
          </div>
        </CardContent>
      </Card>
    );
  }

  return null;
}

client/src/pages/HealthAssessment.tsx.content:
import { useState, useEffect } from 'react';
import { useQuery, useMutation, useQueryClient } from '@tanstack/react-query';
import { Card, CardContent, CardDescription, CardHeader, CardTitle } from '@/components/ui/card';
import { Button } from '@/components/ui/button';
import { Badge } from '@/components/ui/badge';
import { Alert, AlertDescription } from '@/components/ui/alert';
import { Separator } from '@/components/ui/separator';
import { Progress } from '@/components/ui/progress';
import { 
  Brain, 
  Heart, 
  AlertTriangle, 
  CheckCircle, 
  Clock,
  Stethoscope,
  FileText,
  TrendingUp
} from 'lucide-react';
import { apiRequest } from '@/lib/queryClient';
import { SymptomChecker } from '@/components/SymptomChecker';
import { AssessmentHistory } from '@/components/AssessmentHistory';

interface AIHealthAnalysis {
  diagnosis: string[];
  recommendations: string[];
  urgencyLevel: 'low' | 'medium' | 'high' | 'emergency';
  referralNeeded: boolean;
  riskScore: number;
  followUpDays?: number;
  emergencyWarning?: string;
}

interface HealthAssessment {
  id: string;
  assessmentType: string;
  symptoms: string[];
  aiAnalysis: AIHealthAnalysis;
  followUpRequired: boolean;
  consultationRecommended: boolean;
  createdAt: string;
}

interface Questionnaire {
  id: string;
  title: string;
  description: string;
  questions: Array<{
    id: string;
    type: string;
    question: string;
    options?: string[];
    required: boolean;
  }>;
}

export function HealthAssessment() {
  const [activeTab, setActiveTab] = useState<'checker' | 'history'>('checker');
  const [currentAssessment, setCurrentAssessment] = useState<HealthAssessment | null>(null);
  const queryClient = useQueryClient();

  // Fetch available questionnaires
  const { data: questionnairesData } = useQuery({
    queryKey: ['/api/health/questionnaires'],
    enabled: true
  });

  // Emergency symptoms (handled client-side for demo)
  const emergencySymptoms = [
    'chest pain', 'difficulty breathing', 'severe bleeding',
    'loss of consciousness', 'severe allergic reaction'
  ];

  // Fetch recent assessments for the current patient (demo patient for now)
  const { data: assessmentsData } = useQuery({
    queryKey: ['/api/health/assessments/demo-patient-id'],
    enabled: activeTab === 'history'
  });

  const createAssessmentMutation = useMutation({
    mutationFn: async (assessmentData: any) => {
      const response = await apiRequest('POST', `/api/health/assessment`, {
        ...assessmentData,
        patientId: 'demo-patient-id' // Using demo patient ID
      });
      return response.json();
    },
    onSuccess: (data: any) => {
      setCurrentAssessment(data.assessment);
      queryClient.invalidateQueries({ queryKey: ['/api/health/assessments/demo-patient-id'] });
    }
  });

  const handleSymptomSubmission = async (symptomData: any) => {
    try {
      await createAssessmentMutation.mutateAsync(symptomData);
    } catch (error) {
      console.error('Error creating assessment:', error);
    }
  };

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
        return 'destructive';
      case 'high':
        return 'destructive';
      case 'medium':
        return 'secondary';
      case 'low':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getUrgencyIcon = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
      case 'high':
        return <AlertTriangle className="h-4 w-4" />;
      case 'medium':
        return <Clock className="h-4 w-4" />;
      case 'low':
        return <CheckCircle className="h-4 w-4" />;
      default:
        return <Heart className="h-4 w-4" />;
    }
  };

  if (createAssessmentMutation.isPending) {
    return (
      <div className="container mx-auto p-6 space-y-6">
        <div className="text-center space-y-4">
          <Brain className="h-12 w-12 mx-auto animate-pulse text-primary" />
          <h2 className="text-2xl font-bold">AI Health Assessment in Progress</h2>
          <p className="text-muted-foreground">Analyzing your symptoms and generating recommendations...</p>
          <Progress value={65} className="w-full max-w-md mx-auto" />
        </div>
      </div>
    );
  }

  return (
    <div className="container mx-auto p-6 space-y-6">
      {/* Header */}
      <div className="text-center space-y-2">
        <h1 className="text-3xl font-bold flex items-center justify-center gap-2">
          <Brain className="h-8 w-8 text-primary" />
          AI Health Assessment
        </h1>
        <p className="text-muted-foreground">
          Get AI-powered health insights and recommendations based on your symptoms
        </p>
      </div>

      {/* Emergency Warning */}
      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <strong>Emergency Symptoms:</strong> If you experience chest pain, difficulty breathing, 
          severe bleeding, or loss of consciousness, call emergency services immediately.
        </AlertDescription>
      </Alert>

      {/* Navigation Tabs */}
      <div className="flex gap-2 border-b">
        <Button
          variant={activeTab === 'checker' ? 'default' : 'ghost'}
          onClick={() => setActiveTab('checker')}
          className="flex items-center gap-2"
          data-testid="tab-symptom-checker"
        >
          <Stethoscope className="h-4 w-4" />
          Symptom Checker
        </Button>
        <Button
          variant={activeTab === 'history' ? 'default' : 'ghost'}
          onClick={() => setActiveTab('history')}
          className="flex items-center gap-2"
          data-testid="tab-assessment-history"
        >
          <FileText className="h-4 w-4" />
          Assessment History
        </Button>
      </div>

      {/* Content */}
      {activeTab === 'checker' && (
        <>
          {!currentAssessment ? (
            <SymptomChecker
              questionnaires={(questionnairesData as any)?.questionnaires || []}
              onSubmit={handleSymptomSubmission}
              isLoading={createAssessmentMutation.isPending}
            />
          ) : (
            <AssessmentResults 
              assessment={currentAssessment}
              onNewAssessment={() => setCurrentAssessment(null)}
            />
          )}
        </>
      )}

      {activeTab === 'history' && (
        <AssessmentHistory 
          assessments={(assessmentsData as any)?.assessments || []}
          onViewAssessment={setCurrentAssessment}
        />
      )}
    </div>
  );
}

interface AssessmentResultsProps {
  assessment: HealthAssessment;
  onNewAssessment: () => void;
}

function AssessmentResults({ assessment, onNewAssessment }: AssessmentResultsProps) {
  const { aiAnalysis } = assessment;

  const getUrgencyColor = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
        return 'destructive';
      case 'high':
        return 'destructive';
      case 'medium':
        return 'secondary';
      case 'low':
        return 'outline';
      default:
        return 'outline';
    }
  };

  const getUrgencyIcon = (urgency: string) => {
    switch (urgency) {
      case 'emergency':
      case 'high':
        return <AlertTriangle className="h-4 w-4" />;
      case 'medium':
        return <Clock className="h-4 w-4" />;
      case 'low':
        return <CheckCircle className="h-4 w-4" />;
      default:
        return <Heart className="h-4 w-4" />;
    }
  };

  return (
    <div className="space-y-6">
      {/* Header */}
      <Card>
        <CardHeader>
          <CardTitle className="flex items-center gap-2">
            <TrendingUp className="h-5 w-5" />
            Assessment Results
          </CardTitle>
          <CardDescription>
            AI analysis based on your symptoms from {new Date(assessment.createdAt).toLocaleDateString()}
          </CardDescription>
        </CardHeader>
      </Card>

      {/* Emergency Warning */}
      {aiAnalysis.emergencyWarning && (
        <Alert variant="destructive">
          <AlertTriangle className="h-4 w-4" />
          <AlertDescription>
            <strong>Important:</strong> {aiAnalysis.emergencyWarning}
          </AlertDescription>
        </Alert>
      )}

      {/* Urgency & Risk Score */}
      <Card>
        <CardHeader>
          <CardTitle>Health Status Overview</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          <div className="flex items-center justify-between">
            <span className="font-medium">Urgency Level:</span>
            <Badge variant={getUrgencyColor(aiAnalysis.urgencyLevel)} className="flex items-center gap-1">
              {getUrgencyIcon(aiAnalysis.urgencyLevel)}
              {aiAnalysis.urgencyLevel.charAt(0).toUpperCase() + aiAnalysis.urgencyLevel.slice(1)}
            </Badge>
          </div>

          <div className="space-y-2">
            <div className="flex items-center justify-between">
              <span className="font-medium">Risk Score:</span>
              <span className="font-bold">{aiAnalysis.riskScore}/100</span>
            </div>
            <Progress value={aiAnalysis.riskScore} className="w-full" />
          </div>

          {aiAnalysis.followUpDays && (
            <div className="flex items-center justify-between">
              <span className="font-medium">Recommended Follow-up:</span>
              <span>{aiAnalysis.followUpDays} days</span>
            </div>
          )}
        </CardContent>
      </Card>

      {/* Possible Diagnoses */}
      <Card>
        <CardHeader>
          <CardTitle>Possible Conditions</CardTitle>
          <CardDescription>
            These are preliminary assessments. Consult a healthcare provider for proper diagnosis.
          </CardDescription>
        </CardHeader>
        <CardContent>
          <ul className="space-y-2">
            {aiAnalysis.diagnosis.map((diagnosis, index) => (
              <li key={index} className="flex items-start gap-2">
                <div className="w-2 h-2 rounded-full bg-primary mt-2 flex-shrink-0" />
                <span>{diagnosis}</span>
              </li>
            ))}
          </ul>
        </CardContent>
      </Card>

      {/* Recommendations */}
      <Card>
        <CardHeader>
          <CardTitle>Recommendations</CardTitle>
        </CardHeader>
        <CardContent>
          <ul className="space-y-3">
            {aiAnalysis.recommendations.map((recommendation, index) => (
              <li key={index} className="flex items-start gap-2">
                <CheckCircle className="h-4 w-4 text-green-600 mt-0.5 flex-shrink-0" />
                <span>{recommendation}</span>
              </li>
            ))}
          </ul>
        </CardContent>
      </Card>

      {/* Actions */}
      <Card>
        <CardHeader>
          <CardTitle>Next Steps</CardTitle>
        </CardHeader>
        <CardContent className="space-y-4">
          {assessment.consultationRecommended && (
            <Alert>
              <Heart className="h-4 w-4" />
              <AlertDescription>
                A healthcare consultation is recommended based on your symptoms.
              </AlertDescription>
            </Alert>
          )}

          <div className="flex flex-wrap gap-2">
            <Button onClick={onNewAssessment} data-testid="button-new-assessment">
              Start New Assessment
            </Button>
            {assessment.consultationRecommended && (
              <Button variant="outline" data-testid="button-book-consultation">
                Book Consultation
              </Button>
            )}
          </div>
        </CardContent>
      </Card>

      {/* Disclaimer */}
      <Alert>
        <AlertTriangle className="h-4 w-4" />
        <AlertDescription>
          <strong>Medical Disclaimer:</strong> This AI assessment is for informational purposes only 
          and should not replace professional medical advice, diagnosis, or treatment. Always consult 
          with a qualified healthcare provider for medical concerns.
        </AlertDescription>
      </Alert>
    </div>
  );
}

